class Motif
  def self.for(string,substring)
    occurences = []

    string.chars.each_cons(substring.length).each_with_index do |sub, index|
      if sub.join == substring
        occurences << index + 1
      end
    end

    occurences.join(" ")
  end
end

puts "hi"

puts Motif.for("","CGTATCCCG")